<?php	require('../../../../../../wp-blog-header.php');		if(isset($_POST['activate'])){		$mode = 'activate';		$module = $_POST['activate'];	} else {		$mode = 'deactivate';		$module = $_POST['deactivate'];	}		if(isset($module) && !empty($module)){		if($mode == 'activate'){			$active = get_option('reservations_active_modules');			$active[] = $module;			update_option("reservations_active_modules",$active);		} else {			$active = get_option('reservations_active_modules');			if(!empty($active)){				foreach($active as $key => $mod){					if($mod == $module){						unset($active[$key]);						break;					}				}			}			update_option("reservations_active_modules", $active);		}	}	wp_redirect( admin_url().'/admin.php?page=reservation-settings&site=plugins&'.$mode.'='.$module );	exit;?>